#!/bin/bash

alacritty -e yabai -m config layout bsp && yabai -m config top_padding 45 && yabai -m config bottom_padding 12 && yabai -m config left_padding 12 && yabai -m config right_padding 12 && yabai -m config window_gap 6 && yabai -m config mouse_follows_focus on && yabai -m config window_shadow on && yabai -m config window_opacity on && yabai -m config active_window_opacity 1.0 && yabai -m config normal_window_opacity 0.80 && yabai -m config active_window_border_color 0xffD8DEE9 && yabai -m config normal_window_border_color 0xff555555 && yabai -m config mouse_modifier cmd && yabai -m config mouse_action1 move && yabai -m config mouse_action2 resize && yabai -m config mouse_drop_action swap && yabai -m rule --add app="^Alacritty$" mouse_follows_focus=off label=alacrittymouse && yabai -m rule --add app="^Google\ Chrome$" opacity=1.0 label=chromeopacity && yabai -m rule --add app="^Slack$" opacity=1.0 display=2 label=slackdisplay2
